import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const Login = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!formData.email || !formData.password) {
      toast.error('Please fill all required fields');
      return;
    }

    // For demo purposes, we'll just simulate a login
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      // Store some user info in localStorage
      localStorage.setItem('isLoggedIn', 'true');
      setIsLoggedIn(true);
      toast.success('Login successful');
      navigate('/');
    }, 1500);
  };

  const handleMicrosoftLogin = () => {
    toast.info('Microsoft login integration would be implemented here');
  };

  return (
    <div className="login-container">
      <h2 className="login-title">Welcome Back</h2>
      <p className="text-center text-muted mb-4">Sign in to continue to the Student Management System</p>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <button 
          type="submit" 
          className="btn login-btn mb-3"
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
              Logging in...
            </>
          ) : 'Login'}
        </button>
      </form>

      <div className="text-center my-3">or</div>

      <button 
        className="btn ms-btn"
        onClick={handleMicrosoftLogin}
      >
        <i className="fab fa-microsoft me-2"></i>
        Sign in with Microsoft
      </button>

      <div className="mt-4 text-center text-muted small">
        <p>Note: @edu.org.in accounts are allowed</p>
        <p>Your session will only be used for authentication purposes.</p>
      </div>
    </div>
  );
};

export default Login;
